$jsonString = '{
  "samplingInterval": 1000,
  "queueSize": 20,
  "publishingInterval": 10
}'
$dataSets = @(
  @{
    Name = "dataset1Foo"
    DatasetConfiguration = $jsonString
    Topic = @{
      Path = "/path/dataset1"
      Retain = "Keep"
    }
    DataPoint = @(
      @{
        Name = "datapoint1"
        DataSource = "nsu=http://microsoft.com/Opc/OpcPlc/;s=FastUInt1"
        DataPointConfiguration = $jsonString
      }
    )
  }
)

New-AzDeviceRegistryAsset -Name testassetfrompwsh2 -ResourceGroupName test-rg -ExtendedLocationName "/subscriptions/xxxxxxxxxx-xxxx-xxxx-xxxx-xxxxxxxxxx/resourcegroups/test-rg/providers/microsoft.extendedlocation/customlocations/location-xxxxx" -ExtendedLocationType CustomLocation -Location eastus2 -SubscriptionId xxxxxxxxxx-xxxx-xxxx-xxxx-xxxxxxxxxx -AssetEndpointProfileRef myAssetEndpointProfile -DisplayName testassetfrompwsh1 -Manufacturer Contoso123 -ManufacturerUri ContosoModel -ProductCode SA34VDG -Model ContosoModel -SoftwareRevision "2.0" -HardwareRevision "1.0" -SerialNumber "64-103816-519918-8" -DocumentationUri "https://www.example.com/manual" -DefaultTopicPath "/path/defaultTopic" -DefaultTopicRetain "Keep" -DefaultDatasetsConfiguration $jsonString -DefaultEventsConfiguration $jsonString -Dataset $dataSets

$eventGroups = @(
    @{
        name = "eventGroup1"
        dataSource = "nsu=http://microsoft.com/Opc/OpcPlc/EventGroup1"
        eventGroupConfiguration = '{"publishingInterval":10,"samplingInterval":15,"queueSize":20}'
        typeRef = "eventGroup1TypeRef"
        events = @(
            @{
                name = "event1"
                dataSource = "nsu=http://microsoft.com/Opc/OpcPlc/;s=FastUInt5"
                eventConfiguration = '{"publishingInterval":7,"samplingInterval":1,"queueSize":8}'
                destinations = @(
                    @{
                        target = "Mqtt"
                        configuration = @{
                            topic = "/contoso/testEvent1"
                            retain = "Keep"
                            qos = "Qos0"
                            ttl = 7200
                        }
                    }
                )
                typeRef = "event1Ref"
            }
        )
    },
    @{
        name = "eventGroup2"
        events = @(
            @{
                name = "event2"
                dataSource = "nsu=http://microsoft.com/Opc/OpcPlc/;s=FastUInt8"
                eventConfiguration = '{"publishingInterval":7,"samplingInterval":1,"queueSize":8}'
                destinations = @(
                    @{
                        target = "Storage"
                        configuration = @{
                            path = "/tmp/event2"
                        }
                    }
                )
                typeRef = "event2Ref"
            }
        )
    }
)

$managementGroups = @(
    @{
        name = "managementGroup1"
        managementGroupConfiguration = '{"retryCount":10,"retryBackoffInterval":15}'
        typeRef = "managementGroup1TypeRef"
        defaultTopic = "/contoso/managementGroup1"
        defaultTimeoutInSeconds = 100
        actions = @(
            @{
                name = "action1"
                actionConfiguration = '{"retryCount":5,"retryBackoffInterval":5}'
                targetUri = "/onvif/device_service?ONVIFProfile=Profile1"
                typeRef = "action1TypeRef"
                topic = "/contoso/managementGroup1/action1"
                actionType = "Call"
                timeoutInSeconds = 60
            },
            @{
                name = "action2"
                actionConfiguration = '{"retryCount":5,"retryBackoffInterval":5}'
                targetUri = "/onvif/device_service?ONVIFProfile=Profile2"
                typeRef = "action2TypeRef"
                topic = "/contoso/managementGroup1/action2"
                actionType = "Call"
                timeoutInSeconds = 60
            }
        )
    }
)

$datasets = @(
    @{
        name = "dataset1"
        dataSource = "nsu=http://microsoft.com/Opc/OpcPlc"
    },
    @{
        name = "dataSet2"
        dataSource = "nsu=http://microsoft.com/Opc/OpcPlc/Oven;i=5"
        typeRef = "dataset1TypeRef"
        datasetConfiguration = '{"publishingInterval":10,"samplingInterval":15,"queueSize":20}'
        destinations = @(
            @{
                target = "Mqtt"
                configuration = @{
                    topic = "/contoso/test2"
                    retain = "Keep"
                    qos = "Qos1"
                    ttl = 3600
                }
            }
        )
        dataPoints = @(
            @{
                name = "dataset1DataPoint1"
                dataSource = "nsu=http://microsoft.com/Opc/OpcPlc/;s=FastUInt3"
                dataPointConfiguration = '{"publishingInterval":8,"samplingInterval":8,"queueSize":4}'
                typeRef = "dataset1DataPoint1TypeRef"
            },
            @{
                name = "dataset1DataPoint2"
                dataSource = "nsu=http://microsoft.com/Opc/OpcPlc/;s=FastUInt4"
                dataPointConfiguration = '{"publishingInterval":8,"samplingInterval":8,"queueSize":4}'
                typeRef = "dataset1DataPoint2TypeRef"
            }
        )
    }
)

$streams = @(
    @{
        name = "stream1"
        streamConfiguration = '{"publishingInterval":8,"samplingInterval":8,"queueSize":4}'
        typeRef = "stream1TypeRef"
        destinations = @(
            @{
